package basicprogram;

public class IfBlock {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a = 100;
		int b = 10;
		
		if (a<b)  //  a is lesser than b  its false as a is greater than b
		{ 
			System.out.println("Execute 1");
			
		}
		if (a>b)  // a is greater than b = true
		{ 
			System.out.println("Execute 2");
			
		}
		
		if (a>=b)  // a is greater than equal to b =  true
		{ 
			System.out.println("Execute 3");
			
		}
		if (a==b) // a is equal to equal to b it is false
		{ 
			System.out.println("Execute 4");
			
		}
		
		if (a!=b) // true
		{ 
			System.out.println("Execute 5");
			
		}
			
	}

}
